import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  changeText: Boolean;
  changeOutro: Boolean;

  constructor() {
    this.changeText = false;
    this.changeOutro = false;
  }

}
