import { Component } from '@angular/core';
import { WebSocketService } from '../websocket.service';
import { Observable, Subscription, map } from 'rxjs';
import { RxStompService, StompService } from '@stomp/ng2-stompjs';
import { Message } from '@stomp/stompjs';

@Component({
  selector: 'app-websocket',
  templateUrl: './websocket.component.html',
  styleUrls: ['./websocket.component.css']
})
export class WebsocketComponent {

  public receivedMessages: string[] = [];
  //private topicSubscription: Subscription;

  data: Observable<any> | undefined;
  public notifications: any = 0;

  constructor(private rxStompService: RxStompService) {
  }

  ngOnInit() {
    /* this.topicSubscription =  */this.rxStompService.watch('/topic/app').subscribe((message: Message) => {
      this.receivedMessages.push(message.body);
      console.log('>>>> ' + this.receivedMessages);
    });
  }

  ngOnDestroy() {
    /* this.topicSubscription.unsubscribe(); */
  }

  onSendMessage() {
    const message = `Message generated at ${new Date}`;
    this.rxStompService.publish({destination: '/topic/notification', body: message});
    console.log('>>>> ' + this.receivedMessages);
  }

}
